mapper.update:
UPDATE TBL_BOARD_COMMENT_S
SET comment_description_txt = #{commentDescriptionTxt}
WHERE comment_sq = #{commentSq}

UPDATE TBL_BOARD_COMMENT_S
SET comment_is_deleted_yn = 'Y'
WHERE comment_sq = #{commentSq}

        UPDATE TBL_BOARD_COMMENT_S
        SET comment_recommend_cnt = (
	        SELECT COUNT(*) 
	        FROM TBL_RECOMMENDATION_S 
	        WHERE comment_sq = #{commentSq}
	    )
        WHERE comment_sq = #{commentSq}
