mapper.select:
SELECT zonecode, address,
       detail_address, sigungu, latitude, longitude, area_code_sq As areaCodeSq FROM TBL_ADDRESS_S
       WHERE address_sq = #{addressSq}

SELECT zonecode, address,
       detail_address, sigungu, latitude, longitude , area_code_sq FROM TBL_ADDRESS_S WHERE
       address_sq = #{addressSq}

mapper.update:
UPDATE
       TBL_COMPANY_S SET company_url = #{companyUrl}, company_greeting_txt = #{greetingTxt},
       company_is_recruiting_yn = #{isRecruitingYn} WHERE company_sq = #{companySq}
