mapper.select:
SELECT company_nm
FROM TBL_COMPANY_S
WHERE company_sq = #{companySq}

SELECT company_sq
FROM TBL_COMPANY_MEMBER_R
WHERE user_sq = #{userSq}

SELECT user_sq
FROM TBL_COMPANY_MEMBER_R
WHERE company_sq = #{companySq}

SELECT user_sq
FROM TBL_COMPANY_S
WHERE company_sq = #{companySq}

SELECT DISTINCT cm.user_sq FROM TBL_COMPANY_MEMBER_R cm JOIN TBL_RESUME_M r ON cm.user_sq = r.user_sq JOIN TBL_RESUME_SKILL_TAG_S rs ON r.resume_sq = rs.resume_sq WHERE cm.company_sq = #{companySq}
AND rs.skill_tag_nm LIKE CONCAT('%', #{request.keyword}, '%')
AND r.resume_nm LIKE CONCAT('%', #{request.keyword}, '%')
AND ( r.resume_nm LIKE CONCAT('%', #{request.keyword}, '%') OR rs.skill_tag_nm LIKE CONCAT('%', #{request.keyword}, '%') )
LIMIT #{request.size} OFFSET #{request.offset}

SELECT company_biz_num
FROM TBL_COMPANY_S
WHERE user_sq = #{userSq}

SELECT company_biz_num
FROM TBL_COMPANY_S
WHERE company_sq = #{companySq}

SELECT company_member_join_dt
FROM TBL_COMPANY_MEMBER_R
WHERE user_sq = #{userSq}

SELECT company_member_leave_dt
FROM TBL_COMPANY_MEMBER_R
WHERE user_sq = #{userSq}

SELECT company_member_status_cd
FROM TBL_COMPANY_MEMBER_R
WHERE user_sq = #{userSq}

mapper.update:
UPDATE TBL_COMPANY_MEMBER_R
SET company_member_status_cd = #{memberStatusCd}, company_member_leave_dt = NOW()
