mapper.select:
    SELECT *
    FROM TBL_PROJECT_M
    WHERE project_is_deleted_yn = 'N'


	    AND address_sq IN (
	        SELECT address_sq
	        FROM TBL_ADDRESS_S
	        WHERE area_code_sq IN (
	            SELECT area_code_sq
	            FROM TBL_AREA_C
	            WHERE parent_area_code_sq = #{addressCodeSq}
	        )
	    )



        AND project_developer_grade_cd = #{projectDeveloperGradeCd}



        AND project_required_education_cd = #{educationCd}



        AND EXISTS (
		    SELECT 1
		    FROM TBL_PROJECT_RECRUIT_JOB_ROLE_S JR
		    WHERE JR.project_sq = project_sq
		      AND JR.recruit_job_position_type_cd = #{jobRoleCd}
		)





	            AND project_ttl LIKE CONCAT('%', #{searchKeyword}, '%')


	            AND project_writer_name LIKE CONCAT('%', #{searchKeyword}, '%') 



	            AND (
	                project_preference_txt LIKE CONCAT('%', #{searchKeyword}, '%')
	                OR project_description_txt LIKE CONCAT('%', #{searchKeyword}, '%')
	            )


	            AND (
	                project_ttl LIKE CONCAT('%', #{searchKeyword}, '%')
	                OR project_description_txt LIKE CONCAT('%', #{searchKeyword}, '%')
	                OR project_preference_txt LIKE CONCAT('%', #{searchKeyword}, '%')
	            )








	                ORDER BY project_created_at_dtm ${sortOrder}


	                ORDER BY project_view_cnt ${sortOrder}


	                ORDER BY project_candidate_cnt ${sortOrder}


	                ORDER BY project_created_at_dtm DESC




	        ORDER BY project_created_at_dtm DESC





        LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM TBL_PROJECT_M
WHERE project_is_deleted_yn = 'N'


    AND address_sq = #{addressCodeSq}



    AND project_developer_grade_cd = #{projectDeveloperGradeCd}





            AND project_ttl LIKE CONCAT('%', #{searchKeyword}, '%')


            AND project_ttl LIKE CONCAT('%', #{searchKeyword}, '%')



            AND (
                project_ttl LIKE CONCAT('%', #{searchKeyword}, '%')
                OR project_description_txt LIKE CONCAT('%', #{searchKeyword}, '%')
            )








                ORDER BY project_start_dt ${sortOrder}


                ORDER BY project_salary ${sortOrder}


                ORDER BY project_start_dt DESC 




        ORDER BY project_start_dt DESC

SELECT address_sq
FROM TBL_PROJECT_M
WHERE project_sq = #{projectSq}

mapper.update:
UPDATE TBL_PROJECT_M
SET project_candidate_cnt = project_candidate_cnt + 1
WHERE project_sq = #{projectSq}

UPDATE TBL_PROJECT_M
SET project_scrap_cnt = project_scrap_cnt + 1
WHERE project_sq = #{projectSq}

UPDATE TBL_PROJECT_M
SET project_scrap_cnt = project_scrap_cnt - 1
WHERE project_sq = #{projectSq}

UPDATE TBL_PROJECT_M
SET project_view_cnt = project_view_cnt + 1
WHERE project_sq = #{projectSq}

UPDATE TBL_PROJECT_M
SET address_sq = #{newAddressSq}
WHERE project_sq = #{projectSq}
